// src/SelectTimePage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


const SelectTimePage = () => {
  const [preferredTime, setPreferredTime] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Preferred notification time: ${preferredTime}`);
    navigate('/welcome');
  };

  return (
    <div className="flex flex-col items-center justify-between h-screen bg-cover bg-center p-6"
         style={{ backgroundImage: "url('/background.jpg')" }}>
      <div className="text-white text-3xl mt-6 font-bold">
        Select Notification Time
      </div>
      <div className="flex-1 flex flex-col justify-center items-center">
        <form onSubmit={handleSubmit} className="bg-white bg-opacity-80 p-8 rounded-lg shadow-lg">
          <div className="mb-6">
            <label className="block text-gray-700 text-lg font-semibold mb-2">
              Choose Time:
            </label>
            <input
              type="time"
              value={preferredTime}
              onChange={(e) => setPreferredTime(e.target.value)}
              required
              className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded-lg font-semibold hover:bg-blue-700 transition duration-200"
          >
            Submit
          </button>
        </form>
      </div>
      <div className="mb-6">
        <button
          onClick={() => navigate('/')}
          className="bg-gray-500 text-white py-2 px-4 rounded-lg font-semibold hover:bg-gray-600 transition duration-200"
        >
          Back
        </button>
      </div>
    </div>
  );
};

export default SelectTimePage;
