import React, { useState } from 'react';
import './Welcome.css'; // Ensure this CSS file includes the background styling

const quotes = [
  { text: "The best time to plant a tree was 20 years ago. The second best time is now.", background: 'url("/images/tree.jpg")' },
  { text: "Your time is limited, so don’t waste it living someone else’s life.", background: 'url("/images/time.jpg")' },
  { text: "The only way to do great work is to love what you do.", background: 'url("/images/work.jpg")' },
  { text: "Don’t let yesterday take up too much of today.", background: 'url("/images/yesterday.jpg")' },
  { text: "You miss 100% of the shots you don’t take.", background: 'url("/images/shots.jpg")' }
];

const Welcome = () => {
  const [currentQuoteIndex, setCurrentQuoteIndex] = useState(0);

  const handleSwipeUp = () => {
    setCurrentQuoteIndex((prevIndex) => (prevIndex + 1) % quotes.length);
  };

  return (
    <div 
      className="welcome-page" 
      onTouchStart={(e) => {
        const touchStartY = e.touches[0].clientY;
        e.target.addEventListener('touchend', (endEvent) => {
          const touchEndY = endEvent.changedTouches[0].clientY;
          if (touchStartY - touchEndY > 50) {
            handleSwipeUp();
          }
        }, { once: true });
      }}
      style={{ backgroundImage: quotes[currentQuoteIndex].background }}
    >
      <div className="quote">
        <h1>{quotes[currentQuoteIndex].text}</h1>
      </div>
    </div>
  );
};

export default Welcome;
