import React, { useState } from 'react';
import './App.css';

function App() {
  const [preferredTime, setPreferredTime] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Preferred notification time: ${preferredTime}`);
    // Here you would typically send the preferredTime to your backend API
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to Message Daily Quotes</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Enter your preferred time to receive the notification:
            <input
              type="time"
              value={preferredTime}
              onChange={(e) => setPreferredTime(e.target.value)}
              required
            />
          </label>
          <button type="submit">Submit</button>
        </form>
      </header>
    </div>
  );
}

export default App;
