// src/SelectTimePage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import TimePicker from 'react-time-picker';


const SelectTimePage = () => {
  const [preferredTime, setPreferredTime] = useState('10:00');
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Preferred notification time: ${preferredTime}`);
    navigate('/welcome');
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-r from-purple-500 via-pink-500 to-red-500 p-6">
      <div className="text-white text-4xl font-bold mb-10">
        Select Notification Time
      </div>
      <form onSubmit={handleSubmit} className="bg-white bg-opacity-90 p-10 rounded-xl shadow-xl w-full max-w-sm">
        <div className="mb-6">
          <label className="block text-gray-700 text-lg font-semibold mb-2">
            Choose Time:
          </label>
          <TimePicker
            onChange={setPreferredTime}
            value={preferredTime}
            className="w-full px-4 py-3 rounded-lg border border-gray-300"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-gradient-to-r from-purple-600 to-red-600 text-white py-3 rounded-lg font-semibold hover:from-purple-700 hover:to-red-700 transition-all duration-300"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default SelectTimePage;
