import React, { useState } from 'react';

const quotes = [
  { text: "Break those veils that’s hiding Him from you! Break those veils of pride, you women. You’re—you’re King’s daughters; act like it, live like it. 64-0614M — The Unveiling Of God Rev. William Marrion Branham", background: 'url("/img/pexels-achraf-alan-3383361")' },
  { text: "And we come, too, not to heal the sick, but to pray for the sick, and to do what we can to help make life a little easier for each and every one. 62-0704 — We Would See Jesus Rev. William Marrion Branham", background: 'url("/img/pexels-aedrian-7970404")' },
  { text: "The only way to do great work is to love what you do.", background: 'url("/images/work.jpg")' },
  { text: "Don’t let yesterday take up too much of today.", background: 'url("/images/yesterday.jpg")' },
  { text: "You miss 100% of the shots you don’t take.", background: 'url("/images/shots.jpg")' }
];

const Welcome = () => {
  const [currentQuoteIndex, setCurrentQuoteIndex] = useState(0);

  const handleSwipeUp = () => {
    setCurrentQuoteIndex((prevIndex) => (prevIndex + 1) % quotes.length);
  };

  return (
    <div 
      className="welcome-page" 
      onTouchStart={(e) => {
        const touchStartY = e.touches[0].clientY;
        e.target.addEventListener('touchend', (endEvent) => {
          const touchEndY = endEvent.changedTouches[0].clientY;
          if (touchStartY - touchEndY > 50) {
            handleSwipeUp();
          }
        }, { once: true });
      }}
      style={{ backgroundImage: quotes[currentQuoteIndex].background }}
    >
      <div className="quote">
        <h1>{quotes[currentQuoteIndex].text}</h1>
      </div>
    </div>
  );
};

export default Welcome;
